import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import SaveIcon from '@mui/icons-material/Save';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { IconButton } from '@mui/material';
import { addProduct, deleteProduct, setProducts } from '../../store/storeProducts'; // Import actions

const ProductoApp = () => {
    const dispatch = useDispatch();
    const tableData = useSelector((state) => state.products);
    const [item, setItem] = useState({
        nombre: '',
        marca: '',
        tipo: '',
        precio: '',
    });

    const handleInputChange = (event, field) => {
        setItem({
            ...item,
            [field]: event.target.value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3030/addItem', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(item),
            });

            if (response.ok) {
                const newProduct = await response.json();
                dispatch(addProduct(newProduct));
                console.log('Producto guardado con éxito:', newProduct);
            } else {
                const errorText = await response.text();
                console.error('Error al guardar el producto:', errorText);
            }
        } catch (error) {
            console.error('Error de conexión:', error);
        }

        setItem({
            nombre: '',
            marca: '',
            tipo: '',
            precio: '',
        });
    };

    const handleGetItem = async () => {
        try {
            const response = await fetch('http://localhost:3030/getData');
            const data = await response.json();
            dispatch(setProducts(data.data));
        } catch (error) {
            console.error('Error al obtener datos:', error);
        }
    };

    const handleDeleteItem = async (id) => {
        try {
            const response = await fetch('http://localhost:3030/deleteData', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id: id }),
            });

            if (response.ok) {
                dispatch(deleteProduct(id));
            } else {
                console.error('Error al eliminar el producto:', await response.text());
            }
        } catch (error) {
            console.error('Error en handleDeleteItem:', error);
        }
    };

    useEffect(() => {
        handleGetItem();
    }, []);

    return (
        <div>
            <Paper elevation={3} style={{ padding: 20, width: '100%', marginBottom: 20 }}>
                <Box
                    component="form"
                    autoComplete="off"
                    onSubmit={handleSubmit}
                    sx={{ marginBottom: 2, textAlign: 'center' }}
                >
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Nombre"
                                required
                                fullWidth
                                value={item.nombre}
                                onChange={(event) => handleInputChange(event, 'nombre')}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Marca"
                                required
                                fullWidth
                                value={item.marca}
                                onChange={(event) => handleInputChange(event, 'marca')}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Tipo"
                                required
                                fullWidth
                                value={item.tipo}
                                onChange={(event) => handleInputChange(event, 'tipo')}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Precio"
                                required
                                fullWidth
                                value={item.precio}
                                onChange={(event) => handleInputChange(event, 'precio')}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button variant="contained" color="primary" type="submit" startIcon={<SaveIcon />}>
                                Guardar
                            </Button>
                        </Grid>
                    </Grid>
                </Box>
            </Paper>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Nombre</TableCell>
                            <TableCell>Marca</TableCell>
                            <TableCell>Tipo</TableCell>
                            <TableCell>Precio</TableCell>
                            <TableCell>Eliminar</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {tableData.map((rowData) => (
                            <TableRow key={rowData.id}>
                                <TableCell>{rowData.nombre}</TableCell>
                                <TableCell>{rowData.marca}</TableCell>
                                <TableCell>{rowData.tipo}</TableCell>
                                <TableCell>{rowData.precio}</TableCell>
                                <TableCell>
                                    <IconButton onClick={() => handleDeleteItem(rowData.id)}>
                                        <DeleteForeverIcon />
                                    </IconButton>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
};

export default ProductoApp;
