function Login() {
    return (
        <ThemeProvider theme={theme}>
            <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 250 }}>
                        <Avatar style={{ backgroundColor: theme.palette.primary.main, margin: 1 }}>
                            <img src={loginIcon} alt="Login Icon" />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Iniciar sesión
                        </Typography>
                        <form noValidate onSubmit={handleSubmit}>
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                id="email"
                                label="Email"
                                name="email"
                                value={userName}
                                onChange={handleUsernameChange}
                            />
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                name="password"
                                label="Contraseña"
                                type="password"
                                id="password"
                                value={password}
                                onChange={handlePasswordChange}
                            />
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                style={{ margin: 3 }}
                            >
                                Iniciar sesión
                            </Button>
                        </form>
                    </div>
                </Grid>
            </Grid>
        </ThemeProvider>
    );
};

export default Login;
