import React, { useState } from 'react';
import { Grid, Paper, TextField, Button, Avatar, Typography, ThemeProvider } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { loginActions } from './store/storeLogin';
import theme from '../index'; // Importa el tema creado en el index
import loginIcon from './images/loginIcon.png';

const Login = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [userName, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const isVerifiedUser = () => {
    fetch(`http://localhost:3030/login?user=${userName}&password=${password}`) // Asegúrate de que la URL esté dentro de comillas
      .then(response => response.json())
      .then(response => {
        if (response) {
          if (Object.keys(response.data).length === 0) {
            console.log('Datos incorrectos');
          } else {
            dispatch(loginActions.login({
              name: response.data.nombre,
              rol: response.data.rol
            }));
            console.log('Has iniciado sesión');
            navigate('/home');
            console.log(response);
          }
        }
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (userName === '' || password === '') {
      console.log('Campos vacíos');
    } else {
      isVerifiedUser();
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 250 }}>
            <Avatar style={{ backgroundColor: theme.palette.primary.main, margin: 1 }}>
              <img src={loginIcon} alt="Login Icon" />
            </Avatar>
            <Typography component="h1" variant="h5">
              Iniciar sesión
            </Typography>
            <form noValidate onSubmit={handleSubmit}>
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email"
                name="email"
                value={userName}
                onChange={handleUsernameChange}
              />
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                name="password"
                label="Contraseña"
                type="password"
                id="password"
                value={password}
                onChange={handlePasswordChange}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                style={{ margin: 3 }}
              >
                Iniciar sesión
              </Button>
            </form>
          </div>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default Login;
