import React, { useState } from 'react';
import { Box, TextField, Button } from '@mui/material';

const Login = () => {
    const [user, setUser] = useState(''); // Estado para el campo de usuario
    const [password, setPassword] = useState('');

    const handleUserChange = (e) => {
        // Manejar cambios en el campo de usuario
        setUser(e.target.value);
    };

    const handlePasswordChange = (e) => {
        // Manejar cambios en el campo de contraseña
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Evitar la recarga de la página

        // Realizar la llamada a la API
        fetch(`http://localhost:3030/login?user=${user}&password=${password}`)
            .then(response => response.json())
            .then(response => {
                if (response) {
                    console.log(response);
                    // Realizar acciones adicionales según la respuesta
                }
            });
    };

    return (
        <Box
            component="form"
            onSubmit={handleSubmit} // Manejar la presentación del formulario
        >
            <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email"
                name="email"
                value={user}
                onChange={handleUserChange}
            />
            <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                name="password"
                label="Contraseña"
                type="password"
                id="password"
                value={password}
                onChange={handlePasswordChange}
            />
            <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                style={{ margin: 3 }}
            >
                Iniciar sesión
            </Button>
        </Box>
    );
};

export default Login;
