import { createSlice } from '@reduxjs/toolkit';

const productsSlice = createSlice({
    name: 'products',
    initialState: {
        list: [], // Agrega esta propiedad al estado inicial
    },
    reducers: {
        setProducts: (state, action) => {
            state.list = action.payload; // Actualiza la propiedad list en lugar de devolver toda la acción
        },
        addProduct: (state, action) => {
            state.list = [...state.list, action.payload]; // Actualiza la propiedad list al agregar un nuevo producto
        },
        deleteProduct: (state, action) => {
            state.list = state.list.filter((product) => product.id !== action.payload); // Actualiza la propiedad list al eliminar un producto
        },
    },
});

export const { setProducts, addProduct, deleteProduct } = productsSlice.actions;
export default productsSlice.reducer;
