import { createSlice } from '@reduxjs/toolkit';

const productsSlice = createSlice({
    name: 'products',
    initialState: [],
    reducers: {
        setProducts: (state, action) => {
            return action.payload;
        },
        addProduct: (state, action) => {
            const existingProductIndex = state.findIndex(product => product.id === action.payload.id);

            if (existingProductIndex === -1) {
                return [...state, action.payload]; // Return a new array with the added product
            } else {
                // Update the existing product instead of ignoring
                return state.map((product, index) =>
                    index === existingProductIndex ? action.payload : product
                );
            }
        },
        deleteProduct: (state, action) => {
            // Return a new array without the deleted product
            return state.filter(product => product.id !== action.payload);
        },
    },
});

export const { setProducts, addProduct, deleteProduct } = productsSlice.actions;
export default productsSlice.reducer;
