import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { loginActions } from '../store/storelogin';



function Home() {
    //Almacenamos en la variable userData el estado del store
    // Selector LEE LO QUE TIENE GUARDADO EL LOGIN 
    const userData = useSelector(state => state.login) // ACCEDIENDO A "STORELOGIN" PARA VER SI EL USUARIO ESTA AUTENTICAD O NO
    const navigate = useNavigate()
    const dispatch = useDispatch();


    const isLoggedin = userData.isAutenticated

    useEffect(() => {  // comprobar en home si el usuario esta autenticado
        if (!isLoggedin) {
            navigate('/')
        }
    }, [isLoggedin, navigate])

    // log out
    const handleLogout = () => {
        dispatch(loginActions.logout());
        // va a la pag raiz
        navigate('/');
    }
    // Comprobamos por consola qué obtenemos en userData
    console.log(userData)

    return (
        <div>
            <h1>Home de Borja Vega</h1>
            <h2>Nombre de usuario: {userData.userName}</h2>
            <h2>Rol: {userData.userRol}</h2>
            <button onClick={handleLogout}>Salir</button>
        </div>
    );
}
export default Home


