import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Grid, Box } from '@mui/material';
import { ThemeProvider } from '@mui/material/styles';
import { theme } from '../index';
import ResponsiveAppBar from './homeComponents/ResponsiveAppBar';
import FormularioProducto from './homeComponents/FormularioProducto';
import TablaProductos from './homeComponents/TablaProductos';

const Home = () => {
    const userData = useSelector((state) => state.login);
    const navigate = useNavigate();

    useEffect(() => {
        // Comprobar si el usuario está autenticado
        if (!userData.isAutenticated) {
            navigate('/login');
        }
    }, [userData.isAutenticated, navigate]);

    return (
        <ThemeProvider theme={theme}>
            <Box className="background-container">
                <ResponsiveAppBar />
                <Grid container justifyContent="center" alignItems="center" className="form-container">
                    <FormularioProducto />
                </Grid>
                <Grid container justifyContent="center" alignItems="center" className="table-container">
                    <TablaProductos />
                </Grid>
            </Box>
        </ThemeProvider>
    );
};

export default Home;
