import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import Paper from '@mui/material/Paper';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { IconButton } from '@mui/material';
import { deleteProduct, setProducts } from '../../store/storeProducts';

const TablaProductos = () => {
    const dispatch = useDispatch();
    const tableData = useSelector((state) => state.products.list);

    const handleGetItems = async () => {
        try {
            const response = await fetch('http://localhost:3030/getItems');
            const data = await response.json();
            dispatch(setProducts(data.data)); // Dispatch action to set products in the store
        } catch (error) {
            console.error('Error al obtener datos:', error);
        }
    };

    const handleDeleteItem = async (id) => {
        try {
            const response = await fetch('http://localhost:3030/deleteItem?id=' + id);
            const result = await response.json();

            if (result > 0) {
                // Dispatch action to delete the product from the store
                dispatch(deleteProduct(id));
            } else {
                console.error('Error al eliminar el producto:', result);
            }
        } catch (error) {
            console.error('Error en handleDeleteItem:', error);
        }
    };

    useEffect(() => {
        handleGetItems();
    }, []);

    return (
        <>
            <TableContainer component={Paper} style={{ marginTop: '20px' }}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Nombre</TableCell>
                            <TableCell>Marca</TableCell>
                            <TableCell>Tipo</TableCell>
                            <TableCell>Precio</TableCell>
                            <TableCell>Eliminar</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {tableData &&
                            tableData.map((rowData) => (
                                <TableRow key={rowData.id}>
                                    <TableCell>{rowData.nombre}</TableCell>
                                    <TableCell>{rowData.marca}</TableCell>
                                    <TableCell>{rowData.tipo}</TableCell>
                                    <TableCell>{rowData.precio}</TableCell>
                                    <TableCell>
                                        <IconButton onClick={() => handleDeleteItem(rowData.id)}>
                                            <DeleteForeverIcon />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
};

export default TablaProductos;
