import React, { useState } from 'react';
import { Box, TextField, Button } from '@mui/material';
import { theme } from '../index';

const Login = () => {
    const [user, setUser] = useState(''); // Estado para el campo de usuario
    const [password, setPassword] = useState('');

    const handleUserChange = (e) => {
        // Manejar cambios en el campo de usuario
        setUser(e.target.value);
    };

    const handlePasswordChange = (e) => {
        // Manejar cambios en el campo de contraseña
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Evitar la recarga de la página

        // Realizar la llamada a la API
        fetch(`http://localhost:3030/login?user=${user}&password=${password}`)
            .then(response => response.json())
            .then(response => {
                if (response) {
                    console.log(response);
                    // Realizar acciones adicionales según la respuesta
                }
            });
    };

    return (
        <ThemeProvider theme={theme}>
            <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 250 }}>
                        <Avatar style={{ backgroundColor: theme.palette.primary.main, margin: 1 }}>
                            <img src={loginIcon} alt="Login Icon" />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Iniciar sesión
                        </Typography>
                        <Box component="form" noValidate onSubmit={handleSubmit}>
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                id="user"
                                label="Usuario"
                                name="user"
                                value={user}
                                onChange={handleUsernameChange}
                            />
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                name="password"
                                label="Contraseña"
                                type="password"
                                id="password"
                                value={password}
                                onChange={handlePasswordChange}
                            />
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                style={{ margin: 3 }}
                            >
                                Iniciar sesión
                            </Button>
                        </Box>
                    </div>
                </Grid>
            </Grid>
        </ThemeProvider>
    );
};

export default Login;
