import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux'; // Agrega esta importaci贸n
import Paper from '@mui/material/Paper';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { IconButton } from '@mui/material';
import { loginActions } from '../../store/storeLogin';

const TablaProductos = () => {
    const [tableData, setTableData] = useState([]);
    const [item, setItem] = useState({
        nombre: '',
        marca: '',
        tipo: '',
        precio: '',
    });

    const dispatch = useDispatch(); // Agrega este hook de redux

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3030/getData');
            const data = await response.json();
            setTableData(data.data);
        } catch (error) {
            console.error('Error al obtener datos:', error);
        }
    };

    const handleDeleteItem = async (id) => {
        try {
            const response = await fetch('http://localhost:3030/deleteData', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id: id }),
            });
    
            if (response.ok) {
                // Despachar la acci贸n para eliminar el producto en el frontend
                dispatch(loginActions.deleteProduct(id));
            } else {
                console.error('Error al eliminar el producto:', await response.text());
            }
        } catch (error) {
            console.error('Error en handleDeleteItem:', error);
        }
    };
    

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <>
            <TableContainer component={Paper} style={{ marginTop: '20px' }}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Nombre</TableCell>
                            <TableCell>Marca</TableCell>
                            <TableCell>Tipo</TableCell>
                            <TableCell>Precio</TableCell>
                            <TableCell>Eliminar</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {tableData.map((rowData) => (
                            <TableRow key={rowData.id}>
                                <TableCell>{rowData.nombre}</TableCell>
                                <TableCell>{rowData.marca}</TableCell>
                                <TableCell>{rowData.tipo}</TableCell>
                                <TableCell>{rowData.precio}</TableCell>
                                <TableCell>
                                    <IconButton onClick={() => handleDeleteItem(rowData.id)}>
                                        <DeleteForeverIcon />
                                    </IconButton>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
};

export default TablaProductos;
