import React, { useState } from 'react';
import { Box, TextField, Button } from '@mui/material';
import { theme } from '../index';
import { Grid, Paper, Avatar, Typography } from '@mui/material';
import { ThemeProvider } from '@mui/material/styles';

const Login = () => {
    const [user, setUser] = useState(''); // Estado para el campo de usuario
    const [password, setPassword] = useState('');

    const handleUsernameChange = (e) => {
        // Manejar cambios en el campo de usuario
        setUser(e.target.value);
    };

    const handlePasswordChange = (e) => {
        // Manejar cambios en el campo de contraseña
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Evitar la recarga de la página

        // Realizar la llamada a la API
        fetch(`http://localhost:3030/login?user=${user}&password=${password}`)
            .then(response => response.json())
            .then(response => {
                if (response) {
                    console.log(response);
                    // Realizar acciones adicionales según la respuesta
                }
            });
    };

    return (
        <ThemeProvider theme={theme}>
            <Grid
                container
                justifyContent="center"
                alignItems="center"
                style={{
                    height: '100vh',
                    backgroundImage: "url(https://images.unsplash.com/photo-1559999831-7deaf136d4a9?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D)",
                    backgroundSize: 'cover', // Ajusta el tamaño de la imagen de fondo
                }}
            >
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', padding: '2rem' }}>
                        <div>
                            <img src="URL_DE_LA_IMAGEN" alt="Descripción de la imagen" />
                        </div>
                        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 250 }}>
                            <Avatar style={{ backgroundColor: theme.palette.primary.main, margin: 5 }}>
                                <img src={require('./images/loginIcon.png')} alt="Login Icon" />
                            </Avatar>
                            <Typography component="h1" variant="h4">
                                Iniciar sesión
                            </Typography>
                            <Box component="form" noValidate onSubmit={handleSubmit}>
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="user"
                                    label="Usuario"
                                    name="user"
                                    value={user}
                                    onChange={handleUsernameChange}
                                />
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    name="password"
                                    label="Contraseña"
                                    type="password"
                                    id="password"
                                    value={password}
                                    onChange={handlePasswordChange}
                                />
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    color="primary"
                                    style={{ margin: 3 }}
                                >
                                    Iniciar sesión
                                </Button>
                            </Box>
                        </div>
                    </div>
                </Grid>
            </Grid>
        </ThemeProvider>
    );
};

export default Login;
