import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import SaveIcon from '@mui/icons-material/Save';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { IconButton } from '@mui/material';
import { addProduct, deleteProduct, setProducts } from '../../store/storeProducts';

const FormularioProducto = () => {
    const [item, setItem] = useState({
        nombre: '',
        marca: '',
        tipo: '',
        precio: '',
    });
    const [tableData, setTableData] = useState([]);
    const dispatch = useDispatch();

    const handleGetItem = () => {
        fetch('http://localhost:3030/getData')
            .then(response => response.json())
            .then(response => {
                if (response) {
                    setTableData(response.data);
                }
            })
            .catch(error => {
                console.error('Error al obtener datos:', error);
            });
    };

    const handleDeleteItem = (id) => {
        fetch(`http://localhost:3030/deleteItem?id=${id}`)
            .then(response => response.json())
            .then(response => {
                if (response) {
                    if (response < 0) {
                        alert('Error al borrar datos');
                    } else {
                        alert('Datos borrados exitosos');
                        handleGetItem();
                    }
                }
            })
            .catch(error => {
                console.error('Error al intentar eliminar datos:', error);
            });
    };

    const handleSaveItem = (e) => {
        e.preventDefault();
        if (item.nombre.length === 0 || item.marca.length === 0 || item.tipo.length === 0 || item.precio.length === 0) {
            console.log("Datos incompletos");
        } else {
            handleSubmit(e);
            handleGetItem();
        }
    };

    const handleInputChange = (event, field) => {
        setItem((prevItem) => ({
            ...prevItem,
            [field]: event.target.value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(item.nombre);

        fetch(`http://localhost:3030/addItem?nombre=${item.nombre}&marca=${item.marca}&tipo=${item.tipo}&precio=${item.precio}`)
            .then(response => response.json())
            .then(response => {
                if (response > 0) {
                    alert("Operación realizada con éxito");
                } else {
                    console.log("Operación fallida :(");
                }
                handleGetItem();
            })
            .catch(error => {
                console.error('Error al realizar la operación:', error);
            });
    };

    useEffect(() => {
        const fetchItems = async () => {
            try {
                const response = await fetch('http://localhost:3030/getData');
                const data = await response.json();

                // Dispatch action to set products in the store
                dispatch(setProducts(data.data));
            } catch (error) {
                console.error('Error al obtener datos:', error);
            }
        };

        fetchItems();
    }, [dispatch]);

    return (
        <>
            <Paper elevation={3} style={{ padding: 20, width: '100%' }}>
                <Box component="form" autoComplete="off" onSubmit={handleSaveItem} sx={{ marginBottom: 2, textAlign: 'center' }}>
                    <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Nombre"
                                required
                                fullWidth
                                value={item.nombre}
                                onChange={(event) => handleInputChange(event, 'nombre')}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Marca"
                                required
                                fullWidth
                                value={item.marca}
                                onChange={(event) => handleInputChange(event, 'marca')}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Tipo"
                                required
                                fullWidth
                                value={item.tipo}
                                onChange={(event) => handleInputChange(event, 'tipo')}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                label="Precio"
                                required
                                fullWidth
                                value={item.precio}
                                onChange={(event) => handleInputChange(event, 'precio')}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Button variant="contained" color="primary" type="submit" startIcon={<SaveIcon />}>
                                Guardar
                            </Button>
                        </Grid>
                    </Grid>
                </Box>
            </Paper>

            <div style={{ margin: '20px 0' }} />

            <TablaProductos tableData={tableData} handleDeleteItem={handleDeleteItem} />
        </>
    );
};

const TablaProductos = ({ tableData, handleDeleteItem }) => {
    return (
        <TableContainer component={Paper} style={{ marginTop: '20px' }}>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>Nombre</TableCell>
                        <TableCell>Marca</TableCell>
                        <TableCell>Tipo</TableCell>
                        <TableCell>Precio</TableCell>
                        <TableCell>Eliminar</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {tableData.map((rowData) => (
                        <TableRow key={rowData.id}>
                            <TableCell>{rowData.nombre}</TableCell>
                            <TableCell>{rowData.marca}</TableCell>
                            <TableCell>{rowData.tipo}</TableCell>
                            <TableCell>{rowData.precio}</TableCell>
                            <TableCell>
                                <IconButton onClick={() => handleDeleteItem(rowData.id)}>
                                    <DeleteForeverIcon />
                                </IconButton>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

export default FormularioProducto;
