import React, { useState } from 'react';
import { Box, TextField, Button } from '@mui/material';

const Login = () => {
  const [user, setUser] = useState(''); // Estado para el campo de usuario

  const handleUserChange = (e) => {
    // Manejar cambios en el campo de usuario
    setUser(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Evitar la recarga de la página

    // Realizar la llamada a la API
    fetch(`http://localhost:3030/login?user=${user}&password=123456789`)
      .then(response => response.json())
      .then(response => {
        if (response) {
          console.log(response);
          // Realizar acciones adicionales según la respuesta
        }
      });
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit} // Manejar la presentación del formulario
    >
      <TextField
        label="Usuario"
        variant="outlined"
        fullWidth
        value={user}
        onChange={handleUserChange} // Manejar cambios en el campo de usuario
      />
      <Button
        type="submit" // Enviar el formulario
        variant="contained"
        color="primary"
      >
        Iniciar sesión
      </Button>
    </Box>
  );
};

export default Login;
