import React, { useState, useEffect } from 'react';
import Paper from '@mui/material/Paper';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { Button, Box, TextField, Grid, IconButton } from '@mui/material';

const TablaProductos = () => {
    const [tableData, setTableData] = useState([]);
    const [item, setItem] = useState({
        nombre: '',
        marca: '',
        tipo: '',
        precio: '',
    });

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3030/getData');
            const data = await response.json();
            setTableData(data.data);
        } catch (error) {
            console.error('Error al obtener datos:', error);
        }
    };

    const handleSaveItem = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3030/insertData', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(item),
            });

            if (response.ok) {
                // Actualizar la tabla después de la inserción
                fetchData();
                // Limpiar el formulario después de la inserción
                setItem({ nombre: '', marca: '', tipo: '', precio: '' });
            } else {
                console.error('Error al guardar el producto:', await response.text());
            }
        } catch (error) {
            console.error('Error en handleSaveItem:', error);
        }
    };

    const handleDeleteItem = async (id) => {
        try {
            const response = await fetch(`http://localhost:3030/deleteData?id=${id}`);

            if (response.ok) {
                // Actualizar la tabla después de la eliminación
                fetchData();
            } else {
                console.error('Error al eliminar el producto:', await response.text());
            }
        } catch (error) {
            console.error('Error en handleDeleteItem:', error);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <>
            {/* Tu código AppBar y formulario aquí */}

            <TableContainer component={Paper} style={{ marginTop: '20px' }}>
                <Table aria-label="Mi tablita estupenda">
                    <TableHead>
                        <TableRow>
                            <TableCell>Nombre</TableCell>
                            <TableCell>Marca</TableCell>
                            <TableCell>Tipo</TableCell>
                            <TableCell>Precio</TableCell>
                            <TableCell>Eliminar</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {tableData.map((rowData) => (
                            <TableRow key={rowData.id}>
                                <TableCell>{rowData.nombre}</TableCell>
                                <TableCell>{rowData.marca}</TableCell>
                                <TableCell>{rowData.tipo}</TableCell>
                                <TableCell>{rowData.precio}</TableCell>
                                <TableCell>
                                    <IconButton onClick={() => handleDeleteItem(rowData.id)}>
                                        <DeleteForeverIcon />
                                    </IconButton>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
};

export default TablaProductos;
